import React, { Component } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

class Gallery2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tab1: 0,
      isOpen: false,
    };
  }

  render() {
    const { tab1, isOpen } = this.state;
    const { images, tabOne } = this.props;

    return (
      <div>
        {/* Start Portfolio Area */}
        <div className="rn-portfolio-area ptb--120 bg_color--1 line-separator">
          <div className="container">
            <div className="row">
              {images.map((value, index) => (
                <div className="col-lg-6" key={index}>
                  {isOpen && (
                    <Lightbox
                      mainSrc={images[tab1].bigImage}
                      nextSrc={images[(tab1 + 1) % images.length].bigImage}
                      prevSrc={images[(tab1 + images.length - 1) % images.length].bigImage}
                      onCloseRequest={() => this.setState({ isOpen: false })}
                      onMovePrevRequest={() =>
                        this.setState({
                          tab1: (tab1 + images.length - 1) % images.length,
                        })
                      }
                      onMoveNextRequest={() =>
                        this.setState({
                          tab1: (tab1 + 1) % images.length,
                        })
                      }
                    />
                  )}
                  <div onClick={() => this.setState({ isOpen: true, tab1: index })}>
                    <div className="portfolio-static">
                      <div className="thumbnail-inner">
                        <div className="thumbnail">
                          <a href="#portfolio-details">
                            <img src={`/assets/images/portfolio/dp-portfolio-${value.image}.jpg`} alt="Portfolio Images" />
                          </a>
                        </div>
                      </div>
                      <div className="content">
                        <div className="inner">
                          <p>{value.category}</p>
                          <h4>
                            <a href="#portfolio-details">{value.title}</a>
                          </h4>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        {/* End Portfolio Area */}
      </div>
    );
  }
}

export default Gallery2;
