import React, { useState, useEffect } from 'react';

const TypingAnimation = ({ text, elementTag }) => {
  const [typedText, setTypedText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const typingInterval = setInterval(() => {
      if (currentIndex < text.length) {
        const randomDelay = 50; // Random delay between 25 and 100 milliseconds
        setTimeout(() => {
          setTypedText((prevText) => prevText + text[currentIndex]);
          setCurrentIndex((prevIndex) => prevIndex + 1);
        }, randomDelay);
      } else {
        clearInterval(typingInterval);
      }
    }, 100); // Adjust the typing speed as needed (e.g., increase or decrease the interval).

    return () => {
      clearInterval(typingInterval);
    };
  }, [text, currentIndex]);

  const Element = elementTag || 'p';

  return <Element>{typedText}</Element>;
};

export default TypingAnimation;
