import React, { useState } from 'react';
import { FaLinkedinIn, FaFacebookF, FaInstagram } from 'react-icons/fa';
import { Parallax } from 'react-parallax';
import AOS from 'aos';
import 'aos/dist/aos.css'; 

AOS.init();

const getAnimation = (index) => {
    if (index === 0) {
      return "fade-left";
    } else if (index === 1) {
      return "fade-up";
    } else if (index === 2) {
      return "fade-right";
    } else {
      return "fade-up";
    }
  };

// Import any necessary icons here, like FaFacebookF, FaLinkedinIn, and FaInstagram
const image1 = '/assets/images/bg/riverTube2.0.png';
const PopoverExample = () => {
  const [selectedMember, setSelectedMember] = useState(null);

  const data = [
    {
      images: '01',
      title: 'Zach Martin',
      designation: 'President',
      summary: 'Zach Martin, the owner of ATX Float, is a driven entrepreneur whose journey from Florida to Ohio and eventually to Austin, Texas, reflects his commitment to turning a passion into a thriving business...',
      socialNetwork: [
        {
          icon: <FaFacebookF />,
          url: '#'
        },
        {
          icon: <FaLinkedinIn />,
          url: '#'
        },
        {
          icon: <FaInstagram />,
          url: '#'
        },
      ]
    },
    {
      images: '02',
      title: 'Jay Perry',
      designation: 'Lead Sales',
      summary: 'Jay Perry stands as a key figure at ATX Float, where he has consistently led the company&apos;s sales efforts for an impressive five years...',
      socialNetwork: [
        {
          icon: <FaFacebookF />,
          url: '#'
        },
        {
          icon: <FaLinkedinIn />,
          url: '#'
        },
      ]
    },
    {
      images: '03',
      title: 'Morgan Hondros',
      designation: 'Web Architech',
      summary: 'Some gibberish',
      socialNetwork: [
        {
          icon: <FaFacebookF />,
          url: 'https://www.facebook.com/morgan.hondros'
        },
        {
          icon: <FaLinkedinIn />,
          url: 'https://www.linkedin.com/in/morgan-hondros-781878151/'
        },
      ]
    },
  ];

  const getAnimation = (index) => {
    if (index === 0) {
      return 'fade-left';
    } else if (index === 1) {
      return 'fade-up';
    } else if (index === 2) {
      return 'fade-right';
    } else {
      return 'fade-up';
    }
  };

  const handleMemberClick = (memberIndex) => {
    setSelectedMember(memberIndex);
  };

  return (
    <div>
        <Parallax bgImage={image1} strength={800} style={{padding: '70px',}}>
      {/* Your component code here... */}
      <div className="rn-team-area">
                        <div className="container">

      <div className="row" style={{ display: 'flex', justifyContent: 'center' }}>

        {data.map((value, i) => (
          <div key={i}>
            {/* Your component elements here */}
            <h4 className="title" onClick={() => handleMemberClick(i)}>
              {value.title}
            </h4>
            {/* Display other member information based on the `selectedMember` state */}
            {selectedMember === i && (
              <div className="summary">
                <p>{value.summary}</p>
              </div>
            )}
          </div>
        ))}
      </div>
      </div>
      </div>
      </Parallax>
    </div>
  );
};

export default PopoverExample;
