import './Gallery2.css'; // You can create a CSS file for styling

import React, { useState } from 'react';
import { FiArrowLeft, FiArrowRight, FiSearch, FiXCircle } from 'react-icons/fi';

const Gallery2 = ({ images }) => {
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [zoomed, setZoomed] = useState(false);

  const openImage = (index) => {
    setSelectedImageIndex(index);
    setZoomed(true);
  };

  const closeImage = () => {
    setSelectedImageIndex(null);
    setZoomed(false);
  };

  const navigateImages = (direction) => {
    const newIndex =
      direction === 'left'
        ? selectedImageIndex - 1
        : direction === 'right'
        ? selectedImageIndex + 1
        : selectedImageIndex;

    if (newIndex >= 0 && newIndex < images.length) {
      setSelectedImageIndex(newIndex);
    }
  };

  return (
    <div className="gallery">
      {images.map((image, index) => (
        <div key={index} className="gallery-item">
          <div
            className="gallery-image"
            style={{ backgroundImage: `url(${image.imageUrl})` }}
            onClick={() => openImage(index)}
          ></div>
          <div className="gallery-info">
            <h3>{image.productTitle}</h3>
            <p>{image.imageDesc}</p>
          </div>
        </div>
      ))}

      {zoomed && selectedImageIndex !== null && (
        <div className="fullscreen-overlay">
          <div className="fullscreen-image">
            <div
              className="fullscreen-close"
              onClick={() => closeImage()}
            >
              <FiXCircle />
            </div>
            <div
              className="fullscreen-left"
              onClick={() => navigateImages('left')}
            >
              <FiArrowLeft />
            </div>
            <div
              className="fullscreen-right"
              onClick={() => navigateImages('right')}
            >
              <FiArrowRight />
            </div>
            <div className="fullscreen-zoom">
              <FiSearch />
            </div>
            <div className="fullscreen-content">
              <div
                className="fullscreen-image-inner"
                style={{
                  backgroundImage: `url(${images[selectedImageIndex].imageUrl})`,
                }}
              ></div>
              <div className="fullscreen-info">
                <h3>{images[selectedImageIndex].productTitle}</h3>
                <p>{images[selectedImageIndex].imageDesc}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery2;
