import React, { useState } from 'react';
import { FiArrowLeft, FiArrowRight, FiSearch, FiXCircle } from 'react-icons/fi';
import './Gallery2.css'; // You can create a CSS file for styling

const Gallery2 = ({ images }) => {
  const [fullScreen, setFullScreen] = useState(null);

  const openFullScreen = (index) => {
    setFullScreen(index);
  };

  const closeFullScreen = () => {
    setFullScreen(null);
  };

  const goToPrevImage = () => {
    if (fullScreen > 0) {
      setFullScreen(fullScreen - 1);
    }
  };

  const goToNextImage = () => {
    if (fullScreen < images.length - 1) {
      setFullScreen(fullScreen + 1);
    }
  };

  const imageList = images.map((image, index) => (

    <div key={index} className="gallery-image-container">
      <div
        className="gallery-image"
        style={{ backgroundImage: `url(${image.imageUrl})` }}
        onClick={() => openFullScreen(index)}
      >
        <div className="gallery-image-text">
          <h3>{image.productTitle}</h3>
          <p>{image.imageDesc}</p>
        </div>
      </div>
    </div>
  ));

  return (
    <div className="gallery">
      {imageList}
      {fullScreen !== null && (
        <div className="fullscreen-overlay">
          <div className="fullscreen-content">
            <div className="fullscreen-image">
              <img src={images[fullScreen].imageUrl} alt={images[fullScreen].productTitle} />
              <div className="fullscreen-image-text">
                <h3>{images[fullScreen].productTitle}</h3>
                <p>{images[fullScreen].imageDesc}</p>
              </div>
            </div>
            <div className="fullscreen-buttons">
              <button onClick={goToPrevImage}>
                <FiArrowLeft color='black'/>
              </button>
              <button onClick={goToNextImage}>
                <FiArrowRight color='black'/>
              </button>
              <button onClick={closeFullScreen}>
                <FiXCircle color='black'/>
              </button>
              <button>
                <FiSearch color='black'/>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery2;
